import React, { useState } from "react";
// import allCountryScores from "./score";
// import { Toggle } from "@components";

function MakeList() {
  const [list, setList] = useState([
    {
      name: "Ethiopia",
      scores: [
        { n: "Sub", s: 9990 },
        { n: "lucy", s: "4134234" },
        { n: "DWH", s: 9999 },
        { n: "Hanif", s: 999999999 },
      ],
    },
    {
      name: "Scotland",
      scores: [
        { n: "groundkeeper willie", s: 4000 },
        { n: "Neill", s: 999999 },
        { n: "braveheart", s: -200 },
      ],
    },
  ]);

  const [toggle, setToggle] = useState(true);

  const toggleList = (scores) => {
    setToggle(!toggle);
    return toggle
      ? scores.sort((a, b) => b.s - a.s)
      : scores.sort((a, b) => a.s - b.s);
  };

  return (
    <div>
      <>
        <button
          onClick={() =>
            setList(
              list.map((item) => ({ ...item, scores: toggleList(item.scores) }))
            )
          }
        >
          Sort{" "}
        </button>
      </>
      <ul>
        {list.map((element) => (
          <li key={element.name}>
            <h3>High Scores: {element.name}</h3>
            <ul>
              {element.scores.map((score) => (
                <p key={score.n}>
                  {" "}
                  {score.n}
                  {score.s}{" "}
                </p>
              ))}
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
}

