import React, { useState } from "react";
// import allCountryScores from "./score";
// import { Toggle } from "@components";

function MakeList() {
  const [list, setList] = useState([
    {
      name: "Ethiopia",
      scores: [
        { n: "Sub", s: 9990 },
        { n: "lucy", s: "4134234" },
        { n: "DWH", s: 9999 },
        { n: "Hanif", s: 999999999 },
      ],
    },
    {
      name: "Scotland",
      scores: [
        { n: "groundkeeper willie", s: 4000 },
        { n: "Neill", s: 999999 },
        { n: "braveheart", s: -200 },
      ],
    },
  ]);

  const [toggle, setToggle] = useState(true);

  const toggleList = () => {
    setToggle(!toggle);
    setList(
      toggle
        ? [...list].sort((a, b) => b.scores[0].s - a.scores[0].s)
        : [...list].sort((a, b) => a.scores[0].s - b.scores[0].s)
    );
    console.log();
  };

  // const useToggle = (initialState) => {
  //   const [toggleValue, setToggleValue] = useState(initialState);

  //   const toggler = () => {
  //     setToggleValue(!toggleValue);
  //   };
  //   return [toggleValue, toggler];
  // };s

  return (
    <div>
      <>
        <input type="checkbox" onClick={toggleList} />
        Sort
      </>
      <div>
        {list.map((element) => (
          <h3 key={element.name}>High Scores: {element.name}</h3>
        ))}
        <div>
          {list.scores.map((item) => (
            <PLayerScore n={item.scores.n} s={item.scores.s} />
          ))}
        </div>
      </div>
    </div>
  );
}

function PLayerScore(props) {
  return (
    <div>
      <table className="table">
        <tbody>
          <tr>
            <td>{props.n}</td>
            <td>{props.s}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default MakeList;
