import React, { useState } from "react";
import allCountryScores from "./score";
// import { Toggle } from "@components";

function MakeList() {
  const [list, setList] = useState(true);

  const sortIt = () => {
    console.log(allCountryScores);
    list(!setList);

    // if (list === ((a, b) => b.s - a.s)) {
    //   setList((a, b) => a.s - b.s);
    // } else setList((a, b) => b.s - a.s);
  };

  return (
    <div>
      <>
        <input type="checkbox" onClick={() => setList(!list)} />
        Sort
      </>
      <div>
        {allCountryScores
          .sort((a, b) => a.name.localeCompare(b.name))
          .map((element, key) => (
            <>
              <h3 key={key}>High Scores: {element.name}</h3>
              {element.scores.sort({ list }).map((el) => {
                return <PLayerScore n={el.n} s={el.s} />;
              })}
            </>
          ))}
      </div>
    </div>
  );
}

function PLayerScore(props) {
  return (
    <div>
      <table className="table">
        <tbody>
          <tr>
            <td>{props.n}</td>
            <td>{props.s}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default MakeList;
