import React, { useState } from "react";
import allCountryScores from "./score";
// import { Toggle } from "@components";

function MakeList() {
  const [list, setList] = useState(
    allCountryScores.filter((element) => element.scores.s)
  );

  const [toggle, setToggle] = useState(true);

  const toggleList = () => {
    setToggle(!toggle);
    setList(
      toggle
        ? [...list].sort((a, b) => b.scores.s - a.scores.s)
        : [...list].sort((a, b) => a.scores - b.scores)
    );
    console.log();
  };

  // const useToggle = (initialState) => {
  //   const [toggleValue, setToggleValue] = useState(initialState);

  //   const toggler = () => {
  //     setToggleValue(!toggleValue);
  //   };
  //   return [toggleValue, toggler];
  // };s

  return (
    <div>
      <>
        <input type="checkbox" onClick={toggleList} />
        Sort
      </>
      <div>
        {allCountryScores
          .sort((a, b) => a.name.localeCompare(b.name))
          .map((element, key) => (
            <h3 key={key}>High Scores: {element.name}</h3>
          ))}
        <div>
          {list.map((item) => (
            <li key={item.s}>{item.scores.s}</li>
          ))}
        </div>
      </div>
    </div>
  );
}

