    } else setList(allCountryScores.scores.sort((a, b) => b.s - a.s));
  }

  return (
    <div>
      <>
        <button onClick={sortIt}>Sort</button>
      </>
      {allCountryScores
        .sort((a, b) => a.name.localeCompare(b.name))
        .map((element, key) => (
          <>
            <h3 key={key}>High Scores: {element.name}</h3>
            {element.scores
              .sort((a, b) => b.s - a.s)
              .map((el) => {
                return <PLayerScore n={el.n} s={el.s} />;
              })}
          </>
        ))}
    </div>
  );
}

function PLayerScore(props) {
  return (
    <div>
      <table className="table">
        <tbody>
          <tr>
            <td>{props.n}</td>
            <td>{props.s}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default MakeList;
