import React, { useState } from "react";
import allCountryScores from "./score";

function MakeList() {
  const [list, setList] = useState((a, b) => b - a.s);

  function sortIt() {
    console.log(list);
    if (list === allCountryScores.scores.sort((a, b) => b.s - a.s)) {
      setList(allCountryScores.scores.sort((a, b) => a.s - b.s));
    } else setList(allCountryScores.scores.sort((a, b) => b.s - a.s));
  }

  return (
    <div>
      <>
        <button onClick={sortIt}>Sort</button>
      </>
      {allCountryScores
        .sort((a, b) => a.name.localeCompare(b.name))
        .map((element) => (
          <>
            <h3>High Scores: {element.name}</h3>
            {element.scores.sort({ list }).map((el) => (
              <PLayerScore n={el.n} s={el.s} />
            ))}
          </>
        ))}
    </div>
  );
}

function PLayerScore(props) {
  return (
    <div>
      <table className="table">
        <tbody>
          <tr>
            <td>{props.n}</td>
            <td>{props.s}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default MakeList;
